/**
 * The functions that form the functionality of the extension that takes place in the background.
 * @mixin background
 */

import BlockedSiteList from './classes/BlockedSiteList';
import * as interception from './modules/statistics/interception';
import * as storage from './modules/storage/storage';
import UserSettings from  './classes/UserSettings'
import * as constants from'./constants';
import {isInRegexList} from './modules/stringutil';
import {scrubFromHistory} from './modules/browserutil'
import StorageListener from './modules/storage/StorageListener'

/**
 * Unfortunately webrequestlisteners are not able to process asynchronous functions.
 * Therefore we need a global variable to keep track of the state of the extension.
 * @type {boolean}
 * @memberOf background
 */

let isInterceptionOn = true;

/**
 * inits the background by setting the listener to the {@link BlockedSiteList}
 * @memberOf background
 */
export function initBackground(){
    storage.getBlacklist(function (blockedSiteList) {
        replaceListener(blockedSiteList);
    });
}

/* ---------- ---------- Webrequest functions ----------  ---------- */

/**
 * This function updates the webrequestlistener by passing a {@link BlockedSiteList} to it. From this list, the active urls
 * are taken. If the {@link UserSettings}  is on and there are active urls, a new webrequestlistener is added which intercepts
 * the active urls.
 * @param blockedSiteList {BlockedSiteList} the new list of BlockedSites
 * @memberOf background
 */
function replaceListener(blockedSiteList) {
    removeWebRequestListener();
    storage.getSettings(settings_object => {
        let urlList = blockedSiteList.activeUrls;
        if (settings_object.isInterceptionOn() && urlList.length > 0)
            addWebRequestListener(urlList);
    })
}

/**
 * Adds a listener to the onBeforeRequest which blocks all requests to the urls in the urlList
 * @param urlList {Array} the active urls to be intercepted
 * @memberOf background
 */
function addWebRequestListener(urlList) {
    chrome.webRequest.onBeforeRequest.addListener(
        handleInterception
        , {
            urls: urlList
            , types: ["main_frame"]
        }
        , ["blocking"]
    );
}

/**
 * Clears the onBeforeRequest listener. Used to turn the extension off & to update the listener with a new listener.
 * @memberOf background
 */
function removeWebRequestListener() {
    chrome.webRequest.onBeforeRequest.removeListener(handleInterception);
}


/* ---------- ---------- Interception functions ----------  ---------- */

/**
 * function that does everything that should happen when we decide to intercept the current request.
 * Incrementing counters and modifying url's
 * @param details details about the current webrequest
 * @memberOf background
 */
function intercept(details) {
    interception.incrementInterceptionCounter(details.url);
    interception.addToInterceptDateList();
    let redirectLink = constants.zeeguuExLink;
    let params = constants.tdsRedirectParam + details.url;
    return {redirectUrl: redirectLink + params};
}

/**
 * Function which fires when we enter a website on the blockedsite list.
 * If we come from zeeguu and have completed an exercise than we may continue, else we redirect.
 * @param details the details found by the onWebRequestListener about the current webRequest
 * @memberOf background
 */
function handleInterception(details) {
    if (isInterceptionOn && !isInRegexList(constants.whitelist, details.url)) {
        if (constants.exerciseCompleteRegex.test(details.url)) {
            scrubFromHistory(constants.exerciseCompleteParam);
            let url = details.url.replace(constants.exerciseCompleteRegex, "");
            turnOffInterception();
            return {redirectUrl: url};
        } else {
            return intercept(details);
        }
    }
}

/**
 * turns off interception from the background by setting {@link isInterceptionOn} to false and by retrieving
 * the {@link UserSettings}  from {@link storage} and triggering the turnOffFor function
 * @memberOf background
 */
function turnOffInterception() {
    isInterceptionOn = false;
    storage.getSettings(settings_object => {
        settings_object.turnOffFor(settings_object.interceptionInterval, true);
        storage.setSettings(settings_object);
    })
}

/* ---------- ---------- Storage Listener ----------  ---------- */

/**
 * Actual function that is fired upon the change in storage. If the blockedSiteList changes,
 * we update our listener. If the settings change we encorperate this in the background's behaviour.
 * @param changes data passed by the storage.onChanged event
 * @method handleStorageChange
 * @memberOf background
 */
new StorageListener((changes) => {
    if (constants.tds_blacklist in changes) {
        let newBlockedSiteList = BlockedSiteList.deserializeBlockedSiteList(changes[constants.tds_blacklist].newValue);
        replaceListener(newBlockedSiteList);
    }
    if (constants.tds_settings in changes) {
        let newSettings = UserSettings.deserializeSettings(changes[constants.tds_settings].newValue);
        let oldSettings = UserSettings.deserializeSettings(changes[constants.tds_settings].oldValue);
        isInterceptionOn = newSettings.isInterceptionOn();
        if (!newSettings.isInterceptionOn())
            newSettings.reInitTimer();
        else if (!oldSettings || !oldSettings.isInterceptionOn())
            storage.getBlacklist(blockedSiteList => replaceListener(blockedSiteList));
    }
});

import * as constants from '../constants';

export default class BlockedSite {
    /**
     * Basic datatype of the extension. One BlockedSite instance represents one website that the user wants to block.
     * @param {string} urlBase Domain of the BlockedSite
     * @param {string} title Title of the BlockedSite
     * @constructs BlockedSite
     * @class
     */
    constructor(urlBase, title) {
        /** @member {string} BlockedSite#url String with url formatted in the way the google API uses them for webRequest listeners. \n
         *  I.E. " http://www.facebook.com " -> " *://www.facebook.com/* "
         */
        this._url = this.constructUrl(urlBase);
        /** @member {string} BlockedSite#domain Nice (displayable to the user) version of the url field*/
        this._domain = urlBase;
        /** @member {string} BlockedSite#title Title of the tab we find if we were to go to the endpoint of this url*/
        this._name = title;
        /** @member  {string} BlockedSite#icon Html-code which gets the favicon of the page*/
        this._icon = this.constructIcon(urlBase);
        /** @member {boolean} BlockedSite#checkboxVal Is interception enabled for this blockedSite?*/
        this._checkboxVal = true;
        /** @member {int} BlockedSite#counter How many times were we intercepted from this page */
        this._counter = 0;
        /** @member {int} BlockedSite#timeSpent How much time did we waste on this website */
        this._timeSpent = 0;
    }

    /**
     * returns url regex
     * @param url
     * @returns {string} formatted url
     * @function BlockedSite#constructUrl
     */
    constructUrl(url) {
        return "*://" + url + "/*";
    }

    /**
     * @param url
     * @returns {string} html string for url's favicon
     * @function BlockedSite#constructIcon
     */
    constructIcon(url) {
        return "<img style=\"-webkit-user-select: none\" src=\"" + constants.FAVICONLINK + url + "\">"
    }

    set url(url) { this._url = url; }
    get url() { return this._url; }

    set domain(domain) { this._domain = domain; }
    get domain() { return this._domain; }

    set icon(icon) { this._icon = icon; }
    get icon() { return this._icon; }

    set name(name) { this._name = name; }
    get name() { return this._name; }

    set counter(counter) { this._counter = counter; }
    get counter() { return this._counter; }

    set timeSpent(timeSpent) { this._timeSpent = timeSpent; }
    get timeSpent() { return this._timeSpent; }

    set checkboxVal(checkboxVal) { this._checkboxVal = checkboxVal; }
    get checkboxVal() { return this._checkboxVal; }


    /* --------------- --------------- Serialization --------------- --------------- */

    /**
     * @param {BlockedSite} blockedSite BlockedSite to serialize
     * @returns {string} stringified blockedsite
     * @function BlockedSite#serializeBlockedSite
     */
    static serializeBlockedSite(blockedSite) {
        return JSON.stringify(blockedSite);
    }

    /**
     * Parses the JSON object to a BlockedSite object
     * @param {JSON} blockedSite BlockedSite to parse
     * @returns {BlockedSite} parsed Blocked Site
     * @function BlockedSite#parseBlockedSite
     */

    static parseBlockedSite(blockedSite) {
        let b = new BlockedSite();
        b.url = blockedSite._url;
        b.domain = blockedSite._domain;
        b.name = blockedSite._name;
        b.icon = blockedSite._icon;
        b.checkboxVal = blockedSite._checkboxVal;
        b.counter = blockedSite._counter;
        b.timeSpent = blockedSite._timeSpent;
        return b;
    }

    /**
     * This parses a JSON string to a BlockedSite Object
     * @param serializedBlockedSite {string} JSON string containing the BlockedSite
     * @returns {BlockedSite | null} Deserialized BlockedSite
     * @function BlockedSite#deserializeBlockedSite
     */

    static deserializeBlockedSite(serializedBlockedSite) {
        if(serializedBlockedSite != null) {
            let parsed = JSON.parse(serializedBlockedSite);
            return BlockedSite.parseBlockedSite(parsed);
        }
        return null;
    }

}

import BlockedSite from '../classes/BlockedSite'

export default class BlockedSiteList extends Array {

    /**
     * @constructs BlockedSiteList
     * @class
     */
    constructor() {
        super();
        this.__proto__ = BlockedSiteList.prototype;
    }

    get urls() {
        if (this != []) {
            return this.map(function (bs) {
                return bs.url;
            });
        }
        return [];
    }

    get activeUrls() {
        if (this != []) {
            let urlList = this.filterOnChecked();
            if (urlList != []) {
                return urlList.map(function (bs) {
                    return bs.url;
                });
            }
        }
        return [];
    }

    /**
     * Adds the BlockedSite if this doesn't exist in the list yet.
     * @param newBlockedSite {BlockedSite} {@link BlockedSite} to be added
     * @returns {boolean} Returns true if added to the list
     * @function BlockedSiteList#addToList
     */
    addToList(newBlockedSite) {
        let currentUrls = this.urls;
        let unique = currentUrls.every(function (urlFromList) {
            return urlFromList != newBlockedSite.url;
        });
        if (unique) {
            this.push(newBlockedSite);
            return true;
        } else {
            return false;
        }
    }

    /**
     * Adds all BlockedSites to the list
     * @param blockedSiteList {BlockedSiteList} {@link BlockedSite} to be added
     * @function BlockedSiteList#addAllToList
     */
    addAllToList(blockedSiteList) {
        for (let i = 0; i < blockedSiteList.length; i++) {
            this.addToList(blockedSiteList[i]);
        }
    }

    /**
     * removes BlockedSite from the list
     * @param blockedSiteToDelete {BlockedSite}
     * @function BlockedSiteList#removeFromList
     */
    removeFromList(blockedSiteToDelete) {
        let index = this.map(function(e) { return e.domain; }).indexOf(blockedSiteToDelete.domain);
        this.splice(index, 1);
    }

    /**
     * Takes a BlockedSite and updates the corresponding match in the BlockedSiteList
     * @param blockedSite {BlockedSite} BlockedSite to update
     * @function BlockedSiteList#updateInList
     */
    updateInList(blockedSite){
        this.forEach((item, id) => {
            if (item.url === blockedSite.url)
                this[id] = blockedSite;
        });
    }

    /**
     * Filters BlockedSiteList based on checkboxVal of {@link BlockedSite}
     * @returns {BlockedSiteList} filtered BlockedSiteList
     * @function BlockedSiteList#filterOnChecked
     */
    filterOnChecked() {
        if (this != []) {
            return this.filter(function (a) {
                return a.checkboxVal == true;
            });
        }
        return [];
    }

    /* --------------- --------------- Serialization --------------- --------------- */

    /**
     * @param {BlockedSiteList} blockedSiteList BlockedSiteList to serialize
     * @returns {string} stringified BlockedSiteList
     * @function BlockedSiteList#serializeBlockedSiteList
     */
    static serializeBlockedSiteList(blockedSiteList) {
        let toSerialize = blockedSiteList.map(BlockedSite.serializeBlockedSite);
        return JSON.stringify(toSerialize);
    }

    /**
     * Parses the JSON blockedSiteList to a BlockedSiteList object
     * @param {JSON} blockedSiteList BlockedSiteList to parse
     * @returns {BlockedSiteList} parsed BlockedSiteList
     * @function BlockedSiteList#parseBlockedSite
     */
    static parseBlockedSiteList(blockedSiteList) {
        let bl = new BlockedSiteList();
        bl.addAllToList(blockedSiteList);
        return bl;
    }

    /**
     * This parses a JSON string to a BlockedSiteList Object
     * @param serializedBlockedSiteList {string} JSON string containing the BlockedSiteList
     * @returns {BlockedSiteList | null} Deserialized BlockedSiteList
     * @function BlockedSiteList#deserializeBlockedSiteList
     */
    static deserializeBlockedSiteList(serializedBlockedSiteList) {
        if (serializedBlockedSiteList != null) {
            let parsed = JSON.parse(serializedBlockedSiteList);
            parsed = parsed.map(BlockedSite.deserializeBlockedSite);
            return BlockedSiteList.parseBlockedSiteList(parsed);
        }
        return null;
    }

}

import * as constants from '../constants'
import * as storage from '../modules/storage/storage'

export default class UserSettings {

    /**
     * This object holds all the data that is connected to the user's preferences.
     * Furthermore has the functionality to turn the interception part of the of extension on or off.
     * Since the current state is saved here too.
     * @param {string} id parameter for user's uuid (default = undefined)
     * @constructs UserSettings
     * @class
     */
    constructor(id = undefined) {

        /**
         * @typedef {Object} settingsStatus
         * @property {boolean} state Is interception on?
         * @property {Date} setAt Time at which we changed this status object
         * @property {Date} offTill Time until when the interception turned off,
         * less or equal to current time if interception is on
         * @member {settingsStatus} UserSettings#status The current interception status, it has three fields of its own
         */
        this._status = {
            state: true,
            setAt: new Date(),
            offTill: new Date()
        };
        /** @member {object} UserSettings#mode The current mode of the Distraction Shield's interception */
        this._mode = constants.modes.lazy;
        /** @member {int} UserSettings#interceptionInterval Amount of minutes user is allowed to browse after completing an exercise */
        this._interceptionInterval = 1;
        /** @member {string} UserSettings#UUID The user's current UUID, used for statistics and logging */
        this._UUID = id;
        /** @member {boolean} UserSettings#collectData the value of the data collection checkbox on the optionsPage */
        this._collectData = true;
    }

    set interceptionInterval(val) { this._interceptionInterval = val; }
    get interceptionInterval() { return this._interceptionInterval; }

    set mode(newMode) { this._mode = newMode; }
    get mode() { return this._mode; }

    set collectData (collectData) { this._collectData = collectData; }
    get collectData () { return this._collectData; }

    set status(newStatus) { this._status = newStatus; }
    get status() { return this._status; }

    set offTill(time) { this._status.offTill = time; }
    get offTill() { return this._status.offTill; }

    set UUID(uuid) {this._UUID = uuid;}
    get UUID() { return this._UUID;}

    isInterceptionOn() { return this.status.state; }

    /**
     * Turn the interception back on
     * @function UserSettings#turnOn
     */
    turnOn() {
        if (!this.isInterceptionOn()) {
            this.status = {state: true, setAt: new Date(), offTill: new Date()};
        } else {
            console.log("Already turned on, should not happen!");
        }
    }

    /**
     * Turn interception off. The timer variable is for deciding whether we want to initiate a javascript timeout.
     * Which will automatically turn the interception back on after it stops. This nonly happens in the background, because
     * third party pages might close while the timer runs. Which makes the turning off and on of the interception go wrong.
     * @param {boolean} timer should we set a timer, for when to turn on? (true for background, false for other (third party) page)
     * @function UserSettings#turnOff
     */
    turnOff(timer) {
        if (this.isInterceptionOn()) {
            this.status = {state: false, setAt: new Date(), offTill: this.status.offTill};
            if (timer) {
                this.setTimer();
            }
        } else {
            console.log("Already turned off, should not happen!");
        }
    }

    /**
     * Turns extension off for the specified amount of minutes
     * @param {int} minutes amount of minutes that interception needs to be off
     * @param {boolean} timer should we set a timer, for when to turn on? (true for background, false for other (third party) page)
     * @function UserSettings#turnOffFor
     */
    turnOffFor(minutes, timer) {
        let curDate = new Date();
        this.offTill = new Date(curDate.setMinutes(minutes + curDate.getMinutes()));
        this.turnOff(timer);
    }

    /**
     * Turns interception off until 00:00:00 the next day.
     * @param {boolean} timer should we set a timer, for when to turn on? (true for background, false for other (third party) page)
     * @function UserSettings#turnOffForDay
     */
    turnOffForDay(timer) {
        this.offTill = new Date(new Date().setHours(24, 0, 0, 0));
        this.turnOff(timer);
    }

    /**
     * Turn the interception of the extension back on and update the storage to the new settingObject
     * @function UserSettings#turnExtensionBackOn
     */
    turnExtensionBackOn() {
        return () => {
            if (!this.isInterceptionOn()) {
                this.turnOn();
                storage.setSettings(this);
            }
        };
    }

    /**
     * Run a timer until we need to turn interception back on
     * @function UserSettings#setTimer
     * @private
     */
    setTimer() {
        let timerInMS = this.status.offTill - new Date();
        setTimeout(this.turnExtensionBackOn(), timerInMS);
    }

    /**
     * reInitialize the timer that turns interception back on, if there is need for this. Used for example when browser
     * is closed and re-opened.
     * @function UserSettings#reInitTimer
     */
    reInitTimer() {
        if (!this.isInterceptionOn()) {
            if (this.offTill < new Date()) {
                this.turnOn();
                storage.setSettings(this);
            } else {
                this.setTimer();
            }
        }
    }

    /* --------------- --------------- Serialization --------------- --------------- */

    /**
     * @param {UserSettings} settingsObject SettingsObject to serialize
     * @returns {string} stringified UserSettings
     * @function UserSettings#serializeSettings
     */
    static serializeSettings(settingsObject) {
        return JSON.stringify(settingsObject);
    }

    /**
     * @param {object} parsedSettingsObject SettingsObject in object form, needs to be converted to instance of SettingsObject
     * @returns {UserSettings}
     * @function UserSettings#parseSettingsObject
     */
    static parseSettingsObject(parsedSettingsObject) {
        let s = new UserSettings();
        parsedSettingsObject._status.setAt = new Date(parsedSettingsObject._status.setAt);
        parsedSettingsObject._status.offTill = new Date(parsedSettingsObject._status.offTill);
        s.status = parsedSettingsObject._status;
        s.interceptionInterval = parsedSettingsObject._interceptionInterval;
        s.mode = parsedSettingsObject._mode;
        s.collectData = parsedSettingsObject._collectData;
        s.UUID = parsedSettingsObject._UUID;
        return s;
    }

    /**
     * @param {string} serializedSettingsObject Stringified SettingsObject to be deserialized
     * @returns {UserSettings}
     * @function UserSettings#deserializeSettings
     */
    static deserializeSettings(serializedSettingsObject) {
        if (serializedSettingsObject != null) {
            let parsed = JSON.parse(serializedSettingsObject);
            return this.parseSettingsObject(parsed);
        }
        return null;
    }

}

/**
 * All the constants used throughout the extension
 * @module constants
 */

/* ---------- ----- background ----- ---------- */

/* ---------- ----- background ----- ---------- */
export const zeeguuExLink = "https://www.zeeguu.unibe.ch/practice/get-ex";

export const zeeguuExTracker = "www.zeeguu.unibe.ch/practice/get-ex";

export const exerciseCompleteRegex = /(\?|&)tds_exComplete=true/;

export const exerciseCompleteParam = "tds_exComplete=true";

export const tdsRedirectParam = "?tds_redirect=";

/* ---------- ----- optionspage----- ---------- */
export const feedbackLink = "https://docs.google.com/forms/d/e/1FAIpQLScHTpTaGD19HJd4d7k98FV_20-NwRV0dp9xNMWza7_1KsiGag/viewform";

/* ---------- ----- BlockedSite ----- ---------- */
export const FAVICONLINK = "https://www.google.com/s2/favicons?domain=";

/* ---------- ----- export constants for statistics ----- ---------- */

// The time in seconds after which a user is considered to be idle. Important for tracking the amount of time spent on a url.
// Minimum value is 15 seconds.
export const idleTime = 15;

// The frequency in minutes after which the tracker statistics are being saved into the storage.
export const trackerAlarmFrequency = 1.0;

/* ---------- ----- UserSettings, inject ----- ---------- */
/**
 * Extension interception modes.
 * @readonly
 * @enum {lazy | pro} mode
 */
export const modes = {
    lazy: {
        label: "lazy",
        zeeguuText: "You are in lazy mode. Click <a id='originalDestination'>'Skip'</a> any time to continue browsing."
    },
    pro: {
        label: "pro",
        zeeguuText: "You are in pro mode. Complete the exercise and click the 'Take me away!' button when you are done to continue browsing."
    }
};

export const zeeguLoginLink = "https://www.zeeguu.unibe.ch/login";

export const tdsRedirectRegex = /[?]tds_redirect=(.*)/;

export const loginMessage = "Please log in to get productive!";


/* ---------- ----- BlockedSiteList ----- ---------- */
export const newUrlNotUniqueError = "Oops! You already blocked this website: ";

/* ---------- ----- Whitelist ----- ----- ---------- */

/**
 * A list of regexp's. If a url matches one of these it is not supposed to beblocked
 * @type {RegExp[]}
 */
export const whitelist = [
    "_\/chrome\/newtab",
    "oauth.*\?.*client_id",
    "zeeguu.unibe.ch"
];

/* ---------- ----- urlFormatter ----- ---------- */
export const INVALID_URL_MESSAGE = "We unfortunately could not reach the site you are trying to block.\n" +
    "Are you sure the url is correct? \n \n";

export const FILE_NOT_FOUND_ERROR  = 404;
export const SERVER_ERROR = 500;
export const REQUEST_ABORTED_ERROR = 0;

/* -------------------- Logger constants ----------------------- */
// time interval in minutes between sending data to server
export const LOGGING_INTERVAL = 1440;
// time delay between first use of extension and data logging
export const LOGGING_ALARM_DELAY = 720;

/**
 * Logger event types.
 * @readonly
 * @enum {string} event type
 */
export const logEventType = {
    changed:'changed',
    intercepted:'intercepted',
    spent:'spent',
    failed:'failed'
};

/**
 * Log types.
 * @readonly
 * @enum {string} log type
 */

export const logType = {
    statistics:'statistics',
    settings:'settings'
};

/* -------------------- Text Messages ----------------------- */
export const zeeguuInfoText = "Hey, you tried to enter one of the sites you wanted to be protected from!";

/* -------------------- Keypress export constants ----------------------- */
/* ---------- ----- optionsPage/htmlFunctionality ----- ---------- */
export const KEY_ENTER = 13;



/* -------------------- Number export constants ----------------------- */
// The amount of milliseconds in one day
export const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds

/* ---------- ----- turnoffSlider ----- ---------- */
export const MAX_TURN_OFF_TIME = 480;

/* ---------- ----- Storage constants ----- ---------- */
export const tds_all = null;
export const tds_blacklist = "tds_blacklist";
export const tds_settings = "tds_settings";
export const tds_interceptCounter = "tds_interceptCounter";
export const tds_interceptDateList = "tds_interceptDateList";
export const tds_exerciseTime = "tds_exerciseTime";
export const tds_logs = "tds_logs";
export const tds_logfile = "tds_logfile";

import * as constants from '../constants'
import * as storage from '../modules/storage/storage'
import $ from 'jquery';


/**
 * This module injects html to all content script matches in the manifest.
 * @module inject
 */

/**
 * Check if we have come here after tds redirection, if not return, if so get mode and inject html.
 */
function mainFlow() {
    if (!constants.tdsRedirectRegex.test(window.location.href)) return;
    storage.getMode(initBasis);
}

/**
 * Initialize the tds info panel with the proper text based on mode
 * @param {modes} mode used to set the text for the generalInfoText
 */
function initBasis(mode) {
    let message = mode.zeeguuText;
    $.ajax({
        url: chrome.extension.getURL('/assets/html/inject.html'),
        type: "GET",
        timeout: 5000,
        datatype: "html",
        success: function (data) {
            let infoDiv = $.parseHTML(data);
            $("body").after(infoDiv);
            $("#tds_infoDiv").css('max-width', '800px');
            $("#tds_generalInfoText").append(constants.zeeguuInfoText);

            if (window.location.href.indexOf(constants.zeeguLoginLink) != -1) {
                if (mode.label == constants.modes.pro.label) {
                    message = constants.loginMessage;
                } else {
                    message = message +"\n" + constants.loginMessage;
                }
            }
            $("#tds_modeSpecificText").append(message);

            $("#originalDestination").attr("href", extractDestination());
            $("#aikido").attr("src",chrome.extension.getURL('aikido.png'));
        }
    });
}

/**
 * This function extracts the original destination from a get parameter in the current url
 * @returns {String} newUrl The url of the original destination plus a
 * get parameter indicating that the zeeguu exercise was completed. Should never return null!
 */
function extractDestination() {
    let url = window.location.href;
    let results = constants.tdsRedirectRegex.exec(url);
    if (!results || !results[1]) { return null; }
    let newUrl = decodeURIComponent(results[1]);//prevent errors in browsers that dont decode
    newUrl += (/[?]/.test(newUrl) ? "&" : "?") + "tds_exComplete=true";
    return newUrl;
}

mainFlow();

import {getSettings, setSettings} from '../modules/storage/storage'

/**
 * Module to append dataCollectionFrame to a modal container
 * @module dataCollection
 */

/**
 *
 * @param {JQuery|HTMLElement} modalContainer  should be the html element in which the modal frame will be loaded.
 * @param {boolean} staticModal  can be used to prevent the modal from closing on click or esc
 * @param {function} callback  callback which is called when the modal is closed.
 */
export function showDataCollectionModal(modalContainer, staticModal = false, callback = () => {}) {
    modalContainer.unbind();
    if (staticModal)
        modalContainer.modal({
            backdrop: 'static',
            keyboard: false
        });
    modalContainer.on('shown.bs.modal', () => {
        modalContainer.find('.modal-content').load("../html/dataCollectionFrame.html", () => {
            initDataConsentButtons(modalContainer);
        });
    }).modal('show');
    modalContainer.on('hidden.bs.modal', () => {
        modalContainer.find('.modal-content').load("../html/dataCollectionFrame.html");
        callback();
    });
}

/**
 * Inits the checkbox by retrieving the user's current choice on data collection.
 * adds an onchanged listener to the checkbox.
 */
function initDataConsentButtons(modalContainer){
    $("#learn_more").on('click', () => {
        modalContainer.find('.modal-body').load("../html/dataCollectionInfo.html");
    });
    getSettings(settings_object => {
        let allowBox = $("#allowBox");
        allowBox.prop('checked', settings_object.collectData);
        allowBox.change(() => {
            setDataCollection($('#allowBox').is(':checked'));
        });
    })

}

/**
 * Retrieves the current settings from storage, changes the data collection parameter and stores it.
 * @param bool {boolean} the new value for the data collection parameter.
 */
function setDataCollection(bool){
    getSettings(settings_object => {
        settings_object.collectData = bool;
        setSettings(settings_object);
    })
}

export default class Rectangle {
    constructor(height, width) {
        this.height = height;
        this.width = width;
    }

    get surface() {
        return this.height * this.width;
    }
}

