 */

import uuid from 'js-uuid';

/**
 * Generates a UUID using the uuid npm package
 * @returns {string} UUID in string format
 * @method generateUUID
 */
export function generateUUID() {
    return uuid.v1();
}

/**
 * This module is a utility used to construct and initialize instances of the BlockedSite class. This module is the
 * only right way of creating a new BlockedSiteItem.
 * @module blockedSiteBuilder
 */

import {getUrlFromServer} from "./urlFormatter"
import BlockedSite from "../classes/BlockedSite"
import {addBlockedSiteToStorage} from "./storage/storageModifier"

/**
 * Constructs a new blockedSite instance using the given url, and passes it on using a promise
 * The only real right way of creating a new blocked site. For this it uses the BlockedSite and the urlFormatter.
 * It also adds it to the current list of blockedSites.
 * @param {string} newUrl the unformatted url of which to construct a BlockedSite
 * @method createNewBlockedSite
 */
export function createNewBlockedSite(newUrl) {
    return new Promise((resolve, reject) => {
        getUrlFromServer(newUrl, (url, title) => {
            let blockedSite = new BlockedSite(url, title);
            resolve(blockedSite);
        }, (errorMessage) => { reject(errorMessage); });
    });
}

/**
 * constructs a new BlockedSite and instantly adds it to the storage as well
 * @param {string} newUrl the unformatted url of which to construct a BlockedSite
 * @method createBlockedSiteAndAddToStorage
 */
export function createBlockedSiteAndAddToStorage(newUrl) {
    return new Promise((resolve, reject) => {
        createNewBlockedSite(newUrl)
            .then((blockedSite) => {
                return addBlockedSiteToStorage(blockedSite)
                    .catch(error => {
                        reject(error)
                    })
            })
            .catch(error => {
                reject(error)
            });
    });
}

/**
 * This module has some functions we'd like to have in the browser, for special casing things like opening a tab or
 * modifying user-history
 * @module browserutil
 */

/**
 * This function uses the chrome tabs api to see if a tab is open with a certain url.
 * Calls the callback with: the id of the tab if a tab is open with the given url, false otherwise
 * @param {string} url Url to check for
 * @param {function} callback Function to call with the result of the search
 */
export function isOpenTab (url, callback) {
    chrome.tabs.query({},(tabs) => {
        for (let t of tabs) {
            if (t.url == url) {
                callback(t.id);
                return;
            }
        }
        callback(false);
    });
}

/**
 * A function that opens a tab with a given url, only if none is open yet,
 * switches to the already open tab otherwise
 * @param {string} url Url to check for
 * @param {function} callback
 */
export function openTabSingleton(url, callback = () => {}) {
    isOpenTab(url, (result) => {
        if (result === false) {//meaning: tab not yet open
            chrome.tabs.create({url : url});
        } else {
            chrome.tabs.update(result, {active: true});
        }
        callback();
    });
}

/**
 * This function removes (scrubs) all entries in browserhistory that contain the free-text string query
 * @param {String} query
 */
export function scrubFromHistory(query) {
    chrome.history.search({text: query}, (results) => {
        results.forEach((x) => {
            chrome.history.deleteUrl({url: x.url})
        })
    })
}

/**
 * This module is an utility to help get the correct format for dates which are used in the codebase.
 * @module dateutil
 */


/**
 * Converts milliseconds to HH:MM:SS format
 * @param {int} ms amount of milliseconds
 */
export function msToHHMMSS(ms) {
    return new Date(ms).toISOString().substr(11, 8);
}

/**
 * Converts date object to string format
 * @param {Date} date date object to format
 */
export function formatDate(date) {
    return date.getDate() + "/" + (date.getMonth() + 1) + "/" + date.getFullYear();
}

/**
 * returns the current date, formatted in the correct format.
 */
export function getToday() {
    let dateObject = new Date();
    return formatDate(dateObject);
}

import * as storage from '../modules/storage/storage'
import * as constants from '../constants'

/**
 * This module is meant to be used for logging actions of the user and for statistics.
 * This module retrieves the UUID, generated on install, from the storage and uses this to link data to the user anonymously.
 * The module also dumps the logs every specified time interval.
 * This is simply done by clearing the separate log lines and combining them into one file which is stored in the storage.
 * This can be retrieved using the tds_logfile key.
 * Also when given permission by the user (default yes), the data is sent to a specified API.
 * @module logger
 */

/**
 * creates a log format "ID + args + on time"
 * @param event {logEventType} describes the action performed
 * @param trigger {string}  what triggered the event
 * @param value value of event, default = null
 * @param type {logType} type of the event (options, statistics ..)
 */
export function logToFile(event, trigger = '', value = null, type = 'undefined') {
    let time = new Date().toJSON();
    getUUID(id => {
        let data = {id:id, event:event, trigger:trigger, value:value, time:time, type:type};
        storeLog(data);
    });
}

export function setAlarm() {
    chrome.alarms.create('logdump', {delayInMinutes: constants.LOGGING_ALARM_DELAY, periodInMinutes: constants.LOGGING_INTERVAL });
    chrome.alarms.onAlarm.addListener(alarm => {
        if (alarm.name === 'logdump') {
            scheduledLogDump();
        }
    });
}

/**
 * This dumps the log from the storage to the server (if user gave permission?)
 * After that, it clears the logs.
 */
function scheduledLogDump() {
    storage.getSettings(settings_object => {
        storage.getLogs(data => {
            if (settings_object.collectData)
                sendLogsTo(data);
            dumpToFile(data);
            clearLogs();
        });
    });

}

function clearLogs(){
    storage.clearLogs();
}

/**
 * This function dumps the array of logs in a logfile containing the date & logs.
 * @param data
 */
function dumpToFile(data){
    let time = new Date().toJSON();
    let string = `'{"date":${time},"data":${data}}'`;
    storage.setLogFile(string);
}

/**
 * retrieves UUID from userSettings
 * @callback callback for UUID
 */
function getUUID(callback){
    storage.getSettings(data => {
        callback(data.UUID);
    });
}

/**
 * Append new log to existing log.
 */
function storeLog(data){
    storage.getLogs(logs => {
        if (logs != undefined)
            logs.push(data);
        else
            logs = [data];
        storage.setLogs(logs);
    });
}

/**
 * Sends current log file to dst
 * @param data formatted data to be sent
 */
function sendLogsTo(data){
    $.ajax({
        headers: {
            'content-type': 'application/json'
        },
        url: 'http://127.0.0.1:5000/submit', //TODO get API url to send data to
        data: JSON.stringify(data),
        type: "POST"
    });
}

import * as constants from '../../constants';
import * as exerciseTime from './exerciseTime';
import * as storage from '../storage/storage'
import BlockedSiteList from '../../classes/BlockedSiteList'
import StorageListener from "../storage/StorageListener"
import * as logger from '../../modules/logger'

export default class Tracker {

    /**
     * The tracker tracks whether you are currently working on exercises.
     * Every second, the "alarm" is fired, and the url of the current tab is examined.
     * If this url corresponds with the zeeguu url, the time spent on exercises counter is incremented.
     * @constructs Tracker
     * @class
     */
    constructor() {
        /** @member {boolean} Tracker#wasIdle */
        this.wasIdle = false;
        /** @member {BlockedSiteList} Tracker#blockedsites */
        this.blockedsites = new BlockedSiteList();
        /** @member {Date} Tracker#previousTime */
        this.previousTime = new Date();
        /** @member {Tab} Tracker#currentTab */
        this.currentTab = null;
        this.getCurrentTab().then((tab) => this.currentTab = tab).catch(() => this.currentTab = null);
    }

    /**
     * gets the current tab
     * @returns {Promise}
     * @function Tracker#getCurrentTab
     */
    getCurrentTab() {
        return new Promise(function (resolve, reject) {
            chrome.tabs.query({active: true, lastFocusedWindow: true}, function (tabs) {
                if (tabs.length == 1) {
                    resolve(tabs[0]);
                } else {
                    reject("There is no current tab.");
                }
            });
        });
    }

    /**
     * Initialize the alarm, and initialize the idle-checker.
     * @function Tracker#init
     */
    init() {
        this.getBlockedSites();
        new StorageListener(this.handleStorageChange.bind(this));

        this.addIdleListener();
        this.addAlarmListener();
        this.addOnActiveTabChangeListener();
        this.addOnTabUpdateListener();

        this.createTrackerAlarm();
    }

    /**
     * This function is called everytime the time spent on exercises or
     * on a blocked site needs to be updated.
     * This occurs when the trackerAlarm fires, the idleState is updated,
     * the active tab is changed, or the tab is updated.
     * @function Tracker#triggerUpdateTime
     */
    triggerUpdateTime() {
        let tab = this.currentTab;
        let timeSpent = new Date() - this.previousTime;
        this.previousTime = new Date();
        if(!this.wasIdle) {
            if(tab) {
                if (this.compareDomain(tab.url, constants.zeeguuExTracker)) {
                    this.incTimeExercises(timeSpent);
                    logger.logToFile(constants.logEventType.spent, `exercises`, `${timeSpent/1000}`, constants.logType.statistics);
                } else {
                    this.matchToBlockedSites(tab.url).then((site) => {
                        this.incTimeBlockedSite(site, timeSpent);
                        logger.logToFile(constants.logEventType.spent, `${tab.url}`, `${timeSpent/1000}`, constants.logType.statistics);
                    });
                }
            }
        } else {
            this.wasIdle = false;
        }
    }

    /**
     * Creates the tracker alarm which fires every x minutes. x defined by constants.trackerAlarmFrequency
     * @function Tracker#createTrackerAlarm
     */
    createTrackerAlarm(){
        chrome.alarms.create('trackerAlarm', {periodInMinutes: constants.trackerAlarmFrequency});
    }

    /**
     * Function which updates the idle state. If the user goes idle, triggerUpdateTime is fired, and the
     * trackerAlarm is removed.
     * If the user becomes active again, the triggerUpdateTime is fired,
     * and the trackerAlarm is created again.
     * @param idleState state received from the idleListener
     * @function Tracker#updateIdleState
     */
    updateIdleState(idleState) {
        if(idleState == "idle") {
            chrome.alarms.clear("trackerAlarm");
        } else if (idleState == "active") {
            this.wasIdle = true;
            this.createTrackerAlarm();
        }
        this.triggerUpdateTime();
    }

    /**
     * Adds a idleListener to the background to detect whether a user is idle or not.
     * @function Tracker#addIdleListener
     */
    addIdleListener() {
        // When the user does not input anything for 15 seconds, set the state to idle.
        chrome.idle.setDetectionInterval(constants.idleTime);
        chrome.idle.onStateChanged.addListener(this.updateIdleState.bind(this));
    }

    /**
     * Adds an alarmListener to listen for the trackerAlarm. Every time the alarm ticks, the triggerUpdateTime
     * method is fired.
     * @function Tracker#addAlarmListener
     */
    addAlarmListener() {
        chrome.alarms.onAlarm.addListener((alarm) => {
            if(alarm && alarm.name == 'trackerAlarm') {
                this.getCurrentTab().then((tab) => {
                   if(tab.url === this.currentTab.url) {
                       this.triggerUpdateTime();
                   } else {
                       this.currentTab = tab;
                   }
                }).catch(() => {
                    this.currentTab = null;
                })
            }
        });
    }

    /**
     * Adds an onActiveTabChangeListener. When a tab becomes active, the triggerUpdateTime method is called.
     * @function Tracker#addOnActiveTabChangeListener
     */
    addOnActiveTabChangeListener() {
        chrome.tabs.onActivated.addListener((activeInfo) => {
            chrome.tabs.get(activeInfo.tabId, (tab) => {
                if(typeof tab !== 'undefined') {
                    this.triggerUpdateTime();
                    this.currentTab = tab;
                } else {
                    this.currentTab = null;
                }
                if(chrome.runtime.lastError) return;
            })
        });
    }
    /**
     * Adds an onTabUpdateListener. When a tab updates(e.g. a new address is filled in), the triggerUpdateTime method is called.
     * @function Tracker#addOnTabUpdateListener
     */
    addOnTabUpdateListener() {
        chrome.tabs.onUpdated.addListener((tabId, changeInfo, tab) => {
            if(changeInfo.status === 'loading') {
                chrome.tabs.query({active: true}, (tabs) => {
                    if(tabId == tabs[0].id) {
                        this.triggerUpdateTime();
                        this.currentTab = tab;
                    }
                });
            }
        });
    }

    /**
     * Handles the changes received from the storageOnChangedListener.
     * @param changes received from the storageOnChangedListener.
     * @function Tracker#handleStorageChange
     */
    handleStorageChange(changes) {
        if (constants.tds_blacklist in changes) {
            let newBlockedSiteList = BlockedSiteList.deserializeBlockedSiteList(changes[constants.tds_blacklist].newValue);
            // Extract the time-spent values from the this.blockedsites.
            let timeValues = this.retrieveTimeSpent(this.blockedsites);
            // Replace this.blockedsites with the data from the storage
            this.blockedsites = newBlockedSiteList;
            // Put the extracted time-spent values back into this.blockedsites
            this.putBackTimeSpent(timeValues);
        }
    }

    /**
     * Get a list of the time wasted on all BlockedSites from the BlockedSiteList
     * @param {BlockedSiteList} blockedsites
     * @returns {Array}
     * @function Tracker#retrieveTimeSpent
     */
    retrieveTimeSpent(blockedsites) {
        let list = [];
        blockedsites.map((item) => list.push({'domain': item.domain, 'timeSpent': item.timeSpent}));
        return list;
    }

    /**
     * Puts the timeValues previously extracted using the retrieveTimeSpent method back in to the
     * {@link Tracker#blockedsites} field.
     * @param timeValues
     * @function Tracker#putBackTimeSpent
     */
    putBackTimeSpent(timeValues) {
        this.blockedsites.map((blockedSite) => {
            let bSite = timeValues.find((timeValue) => timeValue.domain == blockedSite.domain);
            if (typeof bSite !== 'undefined') blockedSite.timeSpent = bSite.timeSpent;
        });
    }

    /**
     * adds the BlockedSiteList from the storage to {@link Tracker#blockedsites}.
     * @function Tracker#getBlockedSites
     */
    getBlockedSites() {
        storage.getBlacklistPromise().then((result) => {
            this.blockedsites.addAllToList(result);
        });
    }

    /**
     * Increments the time spent on exercises using the exerciseTime module.
     * @param timeSpent the amount by which the time spent should be incremented.
     * @function Tracker#incTimeExercises
     */
    incTimeExercises(timeSpent) {
        exerciseTime.incrementTodayExerciseTime(timeSpent);
    }

    /**
     * Increments the timeSpent for the blockedSite inputted.
     * @param site the blockedSite of which the timeSpent should be incremented.
     * @param timeSpent the amount by which the time spent should be incremented.
     * @function Tracker#incTimeBlockedSite
     */
    incTimeBlockedSite(site, timeSpent) {
        site.timeSpent += timeSpent;
        storage.setBlacklist(this.blockedsites);
    }

    /**
     * Matches the inputted tab to the blocked sites.
     * @param tabActive
     * @returns {Promise} resolves to the matched blockedSite. Rejects if there is no match.
     * @function Tracker#matchToBlockedSites
     */
    matchToBlockedSites(tabActive) {
        return new Promise((resolve, reject) => {
            let match = this.blockedsites.find((site) => this.compareDomain(tabActive, site.domain));
            if (typeof match !== 'undefined') resolve(match);
        });
    }

    /**
     * Compares the domain of an url to another domain using a regex.
     * @param url Url to be compared.
     * @param domain Domain of which a regex is created.
     * @returns {Boolean} containing whether the domain-regex matches the url.
     * @function Tracker#compareDomain
     */
    compareDomain(url, domain) {
        return Tracker.compareUrlToRegex(this.createRegexFromDomain(domain), url);
    };

    /**
     * Creates a regex string which using the domain of an url.
     * @param domain of which a regex should be created.
     * @returns {string} regex string
     * @function Tracker#createRegexFromDomain
     */
    createRegexFromDomain(domain) {
        return "^(http[s]?:\\/\\/)?(.*)" + domain + ".*$";
    };

    /**
     * Compare regex to url.
     * @param regex
     * @param url
     * @returns {Boolean} Returns true if the url matches the regex. False if they do not match.
     * @function Tracker#compareUrlToRegex
     */
    static compareUrlToRegex(regex, url) {
        return RegExp(regex).test(url);
    }

}

import * as storage from '../storage/storage'
import * as dateutil from '../dateutil'

/**
 * Increments the counter for time spent on exercises today with 'amount'.
 * When there the current day does not exist in the storage yet, initialize the counter for this day at 0.
 * @param {int} amount the amount of seconds to be added to the current date
 * @module exerciseTime
 */
export function incrementTodayExerciseTime(amount) {
    storage.getExerciseTimeList().then((list) => {
        let todayDate = dateutil.getToday();
        let today = list.find((record) => record.date == todayDate);
        if (typeof today === 'undefined') {
            list.push({date: todayDate, timeSpent: amount});
        } else {
            today.timeSpent += amount;
        }
        return list;
    }).then((list) => {
        storage.setExerciseTimeList(list);
    });
}

import * as storage from '../storage/storage'
import * as constants from '../../constants'
import * as stringutil from '../stringutil'
import BlockedSiteList from '../../classes/BlockedSiteList'
import * as logger from '../../modules/logger'

/**
 * This module takes care of all the data that needs to be updated when we are intercepted
 * @module interception
 */

/**
 * This method goes through the interceptDateList and count how many times the user was intercepted last day,
 * last week, last month and the total amount of interceptions.
 * @param {Array} dateList the total list with all interceptions on the different days
 */
export function calcInterceptData(dateList) {
    let tmp = dateList;
    let countDay = 0, countWeek = 0, countMonth = 0, countTotal = 0;

    if (tmp !== null) {
        let firstDate = new Date();
        let length = tmp.length;
        for (let i = 0; i < length; i++) {
            let secondDate = new Date(tmp.pop());
            let diffDays = Math.floor(Math.abs((firstDate.getTime() - secondDate.getTime()) / (constants.oneDay)));
            if (diffDays === 0) {
                countDay++;
            }
            if (diffDays <= 7) {
                countWeek++;
            }
            if (diffDays <= 31) {
                countMonth++
            }
            countTotal++;
        }
    }
    return {
        countDay: countDay,
        countWeek: countWeek,
        countMonth: countMonth,
        countTotal: countTotal
    }
}

/**
 * Receives the url from the parameter, and searches the correct blockedSite item from the blockedsite list.
 * Then the interceptioncounter for this item is incremented by 1.
 * Also the global interceptioncounter is incremented by one.
 * @param {string} urlAddress the url to be compared with the blockedsite list to find the correct item to be incremented
 */
export function incrementInterceptionCounter(urlAddress) {
    let blockedSites = new BlockedSiteList();
    storage.getBlacklistPromise().then((result) => {
        blockedSites.addAllToList(result);
        for (let i = 0; i < blockedSites.length; i++) {
            if (stringutil.wildcardStrComp(urlAddress, blockedSites[i].url)) {
                blockedSites[i].counter = blockedSites[i].counter + 1;
                break;
            }
        }
        storage.setBlacklist(blockedSites);
        storage.getInterceptCounter()
            .then(function (output) {
                let counter = output.tds_interceptCounter;
                counter++;
                storage.setInterceptCounter(counter);
            });
    });
    logger.logToFile(constants.logEventType.intercepted, ``, `${urlAddress}`, constants.logType.statistics);
}

/**
 * Adds the current time+date to the saved time+date list
 */
export function addToInterceptDateList() {
    let interceptDateList;
    storage.getInterceptDateList()
        .then(function (result) {
            interceptDateList = result.tds_interceptDateList;
        })
        .then(function () {
            let newDate = new Date().toDateString();
            if (interceptDateList === null) {
                interceptDateList = [newDate];
            } else {
                interceptDateList.push(newDate);
            }
        })
        .then(function () {
            storage.setInterceptDateList(interceptDateList);
        });
}

export default class StorageListener {
    /**
     * Adds a listener which listens for changes in the storage. Page that use data from the storage and need
     * to be updated on change, will implement a function that defines what to do with the changes and pass
     * it on to the constructor of this class. This method will then fire upon the change of storage.
     * @param {function} handleStorageChange The function to fired that takes as parameter the data from the onChanged event
     * @constructs StorageListener
     * @class
     */
    constructor(handleStorageChange) {
        chrome.storage.onChanged.addListener(changes => {
            handleStorageChange(changes)
        });
    }
}

import BlockedSiteList from '../../classes/BlockedSiteList'
import UserSettings    from '../../classes/UserSettings'
