/**
 * Checks for a runtime error.
 * @method handleRuntimeError
 * @private
 */
function handleRuntimeError() {
    if (chrome.runtime.error) {
        console.log("Runtime error.\n" + chrome.runtime.error);
        return false;
    }
    return true;
}

import * as constants from '../../constants'
import {getBlacklistPromise, setBlacklist} from './storage'
import {logToFile} from '../../modules/logger'

/**
 * This module is used for changing stuff inside the storage. This is used for getting, updating and setting
 * data that is supposed to be in the storage. mostly a matter of storage.get -> setData -> storage.set
 * @module storageModifier
 */


/* ----------------  BlockedSiteList/Blacklist Modifications --------------- */

/**
 *
 * @param {BlockedSite} blocked_site The item we want to add to our {@link BlockedSiteList} in the storage
 * @returns {Promise} Promise that is either resolved or rejected. If rejected passes the type of error. (catch with Promise.catch)
 * @method addBlockedSiteToStorage
 */
export function addBlockedSiteToStorage(blocked_site) {
    return getBlacklistPromise().then(blockedSiteList => {
        if (blockedSiteList.addToList(blocked_site)){
            logToFile(constants.logEventType.changed, blocked_site.name,'added', constants.logType.settings);
            return setBlacklist(blockedSiteList);
        } else
            return Promise.reject(constants.newUrlNotUniqueError + blocked_site.domain);
    });
}

/**
 *
 * @param {BlockedSite} blocked_site The item we want to remove from our {@link BlockedSiteList} in the storage
 * @returns {Promise} Promise that is either resolved or rejected. If rejected passes the type of error. (catch with Promise.catch)
 * @method removeBlockedSiteFromStorage
 */
export function removeBlockedSiteFromStorage(blocked_site) {
    return getBlacklistPromise().then(blockedSiteList => {
        blockedSiteList.removeFromList(blocked_site);
        logToFile(constants.logEventType.changed, blocked_site.name, 'removed', constants.logType.settings);
        return setBlacklist(blockedSiteList);
    });
}

/**
 *
 * @param {BlockedSite} blocked_site The item we want to add to update in our {@link BlockedSiteList} in the storage
 * @returns {Promise} Promise that is either resolved or rejected. If rejected passes the type of error. (catch with Promise.catch)
 * @method updateBlockedSiteInStorage
 */
export function updateBlockedSiteInStorage(blocked_site) {
    return getBlacklistPromise().then(blockedSiteList => {
        blockedSiteList.updateInList(blocked_site);
        return setBlacklist(blockedSiteList);
    });
}

/**
 * This module is a utility that is used for functions we use in more than one place in order to do things like comparison
 * @module stringutil
 */

/**
 * used to compare if a string is a substring of, with ReqExp
 * @param {string} str compared with rule
 * @param {string} rule compare str to this
 * @method wildcardStrComp
 */
export function wildcardStrComp(str, rule) {
    return new RegExp("^" + rule.split("*").join(".*") + "$").test(str);
}

/**
 * This function checks if a string url is in a given list of regexes.
 * @see {module:constants.whitelist}
 * @param {RegExp[]} regexList the list to check against
 * @param {String} url The string to check against the regexp's in regexList
 * @returns {Boolean} True if url matches one of the regexp's in regexList, false otherwise
 * @method isInRegexList
 */
export function isInRegexList(regexList, url) {
    regexList = regexList.map((x) => { return new RegExp(x) });
    let matches = regexList.map((x) => { return x.test(url) });
    return matches.reduce((x, y) => { return x || y });
}

/**
 * <pre>
 * General module which contains two functionalities:
 *      1.  It takes string versions of url's and strips them of certain
 *          parts in the url, we do this in order to be able
 *          to format them so we can do GET-requests with them.
 *      2.  We fire the aforementioned GET-request in order to retrieve
 *          an end-point url of a given url. In other words we retrieve
 *          the url we'd find when we type this into the address bar.
 * </pre>
 * It is used throughout the extension by calling the blockedSiteBuilder module. This builder will use this module in order
 * to build the correct blockedSite.
 * @module urlFormatter
 */

import * as constants from '../constants';

/**
 * removes trailing space from url, if nothing is there returns url
 * @param {string} url to strip
 * @returns {string} the newly formatted url
 * @method stripOfFinalSlash
 */
export function stripOfFinalSlash(url) {
    if (url[url.length - 1] == '/') {
        url = url.slice(0, url.length - 1);
    }
    return url;
}

/**
 * removes scheme from url, if nothing is there returns url
 * @param {string} url to strip
 * @returns {string} the newly formatted url
 * @method stripOfScheme
 */
export function stripOfScheme(url) {
    let schemeless = url;
    if (url.indexOf("://") > -1) {
        schemeless = url.split('://')[1];
    }
    schemeless = stripOfFinalSlash(schemeless);
    return schemeless;
}

/**
 * removes port from url, if nothing is there returns url
 * @param {string} url to strip
 * @returns {string} the newly formatted url
 * @method stripOfPort
 */
export function stripOfPort(url) {
    let portless = [];
    if (url.indexOf(":") > -1) {
        let splittedUrl = url.split(':');
        portless.push(splittedUrl[0]);
        splittedUrl.shift();
        splittedUrl = splittedUrl[0].split('/');
        splittedUrl.shift();
        splittedUrl = splittedUrl.join('/');
        portless.push('/' + splittedUrl + '/');
        url = portless.join("");
    }
    url = stripOfFinalSlash(url);
    return url;
}

/**
 * removes filename from url, if nothing is there returns url
 * @param {string} url to strip
 * @returns {string} the newly formatted url
 * @method stripOfFileName
 */
export function stripOfFileName(url) {
    if (url.indexOf("/") > -1) {
        let nameless = url.split("").reverse().join("");
        nameless = nameless.split(['/']);
        let stripped = [];
        for (let i = 1; i < nameless.length; i++) {
            stripped.push('/');
            stripped.push(nameless[i]);
        }
        stripped = stripped.join("").split("").reverse().join("");
        stripped = stripOfFinalSlash(stripped);
        return stripped;
    } else {
        url = stripOfFinalSlash(url);
        return url;
    }
}

/**
 * @param {string} url the url of which we want the domain
 * @returns {string} the domain
 * @method getDomainOnly
 * @private
 */
function getDomainOnly(url) {
    if (url.indexOf("/") > -1) {
        return url.split("/")[0];
    } else {
        return url;
    }
}

/**
 * removes scheme, final slash, port and filename from url, returns tuple of new url and the domain of the url
 * @param {string} url to strip of all
 * @returns {array} a tuple of the actual url and only the domain=: "www.website.com"
 * @method stripOfAll
 */
export function stripOfAll(url) {
    url = stripOfScheme(url);
    url = stripOfFinalSlash(url);
    url = stripOfPort(url);
    url = stripOfFileName(url);
    url = stripOfFinalSlash(url);
    return [url, getDomainOnly(url)];
}

/**
 * @param {string} url the url to be formatted
 * @returns {string} the formatted url
 * @method formatForGetRequest
 * @private
 */
function formatForGetRequest(url) {
    let strippedUrl = stripOfAll(url);
    return "http://" + strippedUrl[0];
}

/**
 * fires any valid url and gets the end destination's url and page title. These are passed as arguments in the callback
 * @param {string} url the url to check
 * @param {function} onSuccess the callback function that takes the newly formatted end-url together with it's page title as argument. This results in resolving the promise
 * @param {function} onFailure the callback function that when we encounter an error during the asynchronous request. This results in rejecting the promise
 * Used in the BlockedSiteBuilder
 * @method getUrlFromServer
 */
export function getUrlFromServer(url, onSuccess, onFailure) {
    let urlToGet = formatForGetRequest(url);
    let resolve = function (url, title) {
        url = stripOfScheme(url);
        url = stripOfFileName(url);
        onSuccess(url, title);
    };
    httpGetAsync(urlToGet, resolve, onFailure);
}

/**
 * @param {string} theUrlToGet the url to get
 * @param {function} onSuccess the callback function that takes the newly formatted end-url together with it's page title as argument. This results in resolving the promise
 * @param {function} onFailure the callback function that when we encounter an error during the asynchronous request. This results in rejecting the promise
 * fire asynchronous get request to find the end port of the passed url
 * @method httpGetAsync
 */
function httpGetAsync(theUrlToGet, onSuccess, onFailure) {
    let xmlHttp = new XMLHttpRequest();
    xmlHttp.open("GET", theUrlToGet, true); // true for asynchronous
    xmlHttp.onreadystatechange = function () {
        readyStateChange(xmlHttp, onSuccess, onFailure);
    };
    xmlHttp.send(null);
}

/**
 * @param {XMLHttpRequest} xmlHttp the xmlhttprequest to fire
 * @param {function} onSuccess the callback function that takes the newly formatted end-url together with it's page title as argument. This results in resolving the promise
 * @param {function} onFailure the callback function that when we encounter an error during the asynchronous request. This results in rejecting the promise
 * function to be passed to the get-request
 * @method readyStateChange
 * @private
 */
function readyStateChange(xmlHttp, onSuccess, onFailure) {
    if (xmlHttp.readyState == 4 && xmlHttp.status == 200) {
        // simple regex to extract data from title tags, ignoring newlines, tabs and returns
        let titleTags = (/<title.*?>(?:[\t\n\r]*)([\w\W]*?)(?:[\t\n\r]*)<\/title>/m).exec(xmlHttp.responseText);
        if (titleTags != null) {
            let title = titleTags[1];
            onSuccess(xmlHttp.responseURL, title);
        } else {
            onSuccess(xmlHttp.responseURL, theUrlToGet);
        }
    } else if (xmlHttp.readyState == 4) {
        onFailure(errorHandler(xmlHttp.status));
    }
}

/**
 * internal error handler
 * @method errorHandler
 * @private
 */
function errorHandler(status) {
    switch (status) {
        case constants.FILE_NOT_FOUND_ERROR:
            return (constants.INVALID_URL_MESSAGE + 'File not found');
            break;
        case constants.SERVER_ERROR:
            return (constants.INVALID_URL_MESSAGE + 'Server error');
            break;
        case constants.REQUEST_ABORTED_ERROR:
            return (constants.INVALID_URL_MESSAGE + 'Request aborted');
            break;
        default:
            return (constants.INVALID_URL_MESSAGE + 'Unknown error ' + status);
    }
}

import {removeBlockedSiteFromStorage, updateBlockedSiteInStorage} from '../../modules/storage/storageModifier'
import {logToFile} from '../../modules/logger'
import * as constants from '../../constants.js'

/**
 * The table holding the blockedSites and all of its functionality.
 * Every row has the corresponding BlockedSite Object attached to it in order to have easy acces and manipulation.
 *
 * @class BlacklistTable
 * @param {html} html_element the <.table> element to which we connect this BlacklistTable
 */
export default class BlacklistTable {
    constructor(html_element) {
        this.table = html_element;
        this.setCheckboxFunction();
        this.setDeleteButtonFunction();
    }

    /**
     * append html table-row to the table
     * @param tableRow
     * @function BlacklistTable#addToTable
     */
    addToTable(tableRow) {
        tableRow.appendTo(this.table);
    }

    /**
     * applies fadeIn effect to the adding of the table row
     * @param tableRow
     * @function BlacklistTable#addToTableWithFadeIn
     */
    addToTableWithFadeIn(tableRow) {
        tableRow.hide().appendTo(this.table).fadeIn();
    }

    /**
     * removes all rows from the table
     * @function BlacklistTable#removeAllFromTable
     */
    removeAllFromTable(){
        this.table.find('tr:not(:has(th))').remove();
    }

    /**
     * this function removes all table rows, compares the old list with the new list and adds all and fades in new elements.
     * @param newList {BlockedSiteList} the new list of blockedsites
     * @param oldList {BlockedSiteList} the old list of blockedsites
     * @function BlacklistTable#render
     */
    render(newList, oldList){
        this.removeAllFromTable();
        newList.forEach((value, key) => {
            if (key in oldList)
                this.addToTable(this.generateTableRow(value));
            else
                this.addToTableWithFadeIn(this.generateTableRow(value));
        });
    }


    /**
     * set functionality for all checkboxes found in every table_row
     * @function BlacklistTable#setCheckboxFunction
     */
    setCheckboxFunction() {
        this.table.on('change', 'input[type="checkbox"]', data => {
            //Clicking the checkbox automatically selects the row, so we use this to our advantage
            let clicked_checkbox = data.target;
            let selected_row = $(clicked_checkbox).closest('tr');
            let selected_blockedSite = selected_row.data('blockedSite');
            selected_blockedSite.checkboxVal = !selected_blockedSite.checkboxVal;
            logToFile(constants.logEventType.changed, selected_blockedSite.name, (selected_blockedSite.checkboxVal ? 'enabled' : 'disabled'), constants.logType.settings);
            updateBlockedSiteInStorage(selected_blockedSite);
        });
    }

    /**
     * If a delete button is clicked, the closest tr element is deleted.
     * @function BlacklistTable#setDeleteButtonFunction
     */
    setDeleteButtonFunction() {
        this.table.on('click', '.delete-button', data => {
            let clicked_button = data.target;
            let rowToDelete = $(clicked_button).closest('tr');
            let blockedSiteToDelete = rowToDelete.data('blockedSite');
            rowToDelete.fadeOut(() => removeBlockedSiteFromStorage(blockedSiteToDelete));
        });
    }

    /**
     * Takes a BlockedSiteObject and constructs a table row from it, together with attaching this item to the row.
     * @param {BlockedSite} blockedSite the item for which we want to construct a TableRow
     * @returns Table row to be inserted into the table.
     * @function BlacklistTable#generateTableRow
     */
    generateTableRow(blockedSite) {
        let tableRow =
            $("<tr class='table-row' >" +
                "<td width='50'>" + blockedSite.icon + "</td>" +
                "<td class='pageTitle'>" + blockedSite.name + "</td>" +
                "<td width='25'>" + "<input class='checkbox-toggle' type='checkbox' name='state'>" + "</td>" +
                "<td width='25'>" + "<img class='delete-button' type='deleteButton' src='/assets/images/tableRow_delete_button.png' width='16' height='16'>" + "</td>" +
                "</tr>");
        tableRow.find('.checkbox-toggle').prop('checked', blockedSite.checkboxVal);
        //add the actual object to the html_element
        tableRow.data('blockedSite', blockedSite);
        return tableRow;
    }
}

import * as constants from "../../constants"
import {logToFile} from '../../modules/logger'


/**
 * class that connects a <.div> with a span and slider together with all the functionality.
 * I.E. changing colour, updating eachother's values and functionality to be added to the html_elements.
 * The structure this class wants to receive is:
 *      <pre>
 *      <.div id=sliderID>, holding all other components
 *          <.input type=range id=sliderID + "-range">
 *          <.span id=sliderID + "-value">
 *      <./div>
 *      </pre>
 *
 * @abstract
 * @class GreenToRedSlider
 * @param sliderID {string} The ID of the sliderDiv
 * @param saveFunction {function} The function that takes the newly updated value and does what is supposed to happen with it
 */
export default class GreenToRedSlider {

    constructor(sliderID, saveFunction) {
        if (new.target === GreenToRedSlider) {
            throw new TypeError("Cannot construct instances directly, GreenToRedSlider is an abstract class");
        }
        this.saveValue = saveFunction;
        this.sliderDiv = $(sliderID);
        this.sliderRange = $(this.sliderDiv.find(sliderID + "-range"));
        this.sliderValue = $(this.sliderDiv.find(sliderID + "-value"));

        this.setOnEventFunc();
    }

    /**
     * sets all event functions for the GreenToRedSlider
     * @function GreenToRedSlider#setOnEventFunc
     * @inner
     */
    setOnEventFunc() {
        this.sliderRange.on('input', () => {
            let inputValue = this.sliderRange.val();
            this.sliderValue.html(this.calculateHours(inputValue));
            this.updateColor(inputValue);
        });

        this.sliderRange.on('mouseup', () => {
            let inputValue = this.sliderRange.val();
            logToFile(constants.logEventType.changed, `${this.constructor.name}`, `${inputValue}`, constants.logType.settings);
            this.saveValue(inputValue);
        });

        this.sliderValue.on('blur', () => {
            this.checkTimeValidity($(this.sliderValue).html());
        });

        this.sliderValue.keydown(event => {
            if (event.keyCode === constants.KEY_ENTER) {
                this.sliderValue.blur();
                event.preventDefault();
            }
        });
    }

    /**
     * Update the colour of the slider according to the inputValue
     * @param {int} inputValue
     * @function GreenToRedSlider#updateColor
     * @inner
     */
    updateColor(inputValue) {
        let maxSliderVal = (this.sliderRange[0]).max;
        let redVal = Math.round(inputValue / maxSliderVal * 120);
        let greenVal = 120 - redVal;
        this.sliderRange.css('background', 'rgb(' + redVal + ', ' + greenVal + ',0)');
    }

    /**
     * sets the updated value to all elements
     * @param {int} val the new value
     * @function GreenToRedSlider#setValue
     * @inner
     */
    setValue(val) {
        this.sliderRange.val(val);
        this.sliderValue.html(this.calculateHours(val));
        this.updateColor(val);
    }

    /**
     * formats a value in minutes to Hours and minutes.
     * @param {int} val the value to be formatted
     * @returns {string} the formatted string
     * @function GreenToRedSlider#calculateHours
     * @inner
     */
    calculateHours(val) {
        let hours = Math.floor(val / 60);
        let minutes = val % 60;
        if (minutes < 10 && hours > 0)
            minutes = "0" + minutes;
        return this.createMessage(hours, minutes, val);
    }

    createMessage(hours, minutes){
        return (hours > 0 ? hours + ":" + minutes + " hours" : minutes + " minute(s)");
    }

    /**
     * function that checks the validity of the value inputted in the editable span
     * @param {string} val string value that was inputted that needs to be checked whether correct
     * @function GreenToRedSlider#checkTimeValidity
     * @inner
     */
    checkTimeValidity(val) {
        let regex = (/(\d+|\d\:\d{2})(?:\s*)(h(?:our)?s?|m(?:inute|in)?s?|$)/m).exec(val);
        if (regex !== null) {
            if (regex[1].match(":")) {
                let split = regex[1].split(":");
                regex[1] = parseInt(split[0]) + parseInt(split[1]) / 60;
            }
            if (regex[1] > 0) {
                if (regex[2].match("hour")) {
                    regex[1] *= 60;
                }
                // round minutes to a valid number.
                regex[1] = Math.round(regex[1]);
                this.setValue(this.sliderRange[0].max < regex[1] ? this.sliderRange[0].max : regex[1]);
            } else {
                this.timeInputError(val);
            }
        } else {
            this.timeInputError(val);
        }
    }

    /**
     * error function to be fired when we fail the checkTimeValidity function
     * @param {string} val string value that was inputted that needs to be checked whether correct
     * @function GreenToRedSlider#timeInputError
     * @inner
     */
    timeInputError(val) {
        this.setValue(this.sliderRange.val());
        chrome.extension.getBackgroundPage().alert("please input a supported time format");
        logToFile(constants.logEventType.failed, 'slider time input', val, constants.logType.settings);
    }
}

